import React from "react";
import skills from "../json/skills.json";
import { useState, useEffect } from "react";
import Skill from "./Skill";

function SkillsSection() {
    const [data, setData] = useState([])
    const [playOnce, setplayOnce] = useState(true)
    const [id, setId] = useState("tecnical")

    useEffect(() => {
        if(playOnce) {
            setData(skills);
            setplayOnce(false);
        } 

        setData(() => skills.filter(e => e.type === id))

    },[playOnce, id, data]);

    const elem = document.getElementsByClassName("skills-selectioner-animation")

    const left = document.getElementsByClassName("skills-selectioner-tecnical")
    for (let index = 0; index < left.length; index++) {
        elem.addEventListener('click', function () {
            elem.classList.remove("from-left")
            elem.classList.add("from-right")
        });

    }

    const right = document.getElementsByClassName("skills-selectioner-soft")
    for (let index = 0; index < right.length; index++) {
        elem.addEventListener('click', function () {
            elem.classList.remove("from-right")
            elem.classList.add("from-left")
        });

    }

    return(
        <section name="section3" className="skills-page">
            <div className="skills-intro-container">
                <div className="skills-title-selection-container">
                    <div className="skills-title">Skills</div>
                    <div className="skills-selection-container">
                        {/*<div className="skills-selectioner" id="tecnical" onClick={() => setId("tecnical")}>tecnical</div>
                        <div className="skills-selectioner" id="soft" onClick={() => setId("soft")}>soft</div>*/}
                        <div className="skills-selectioner-animation" ></div>
                        <p className="skills-selectioner-tecnical" onClick={() => {setId("tecnical");}}>tecnical</p>
                        <p className="skills-selectioner-soft" onClick={()=> {setId("soft");}}>soft</p>
                    </div>
                </div>
            </div>
            <div className="skills-items-container">
                <ul className="skills-items-list">
                    {data.map((e) => (<div key={e.title} className="skills-list-elem"><Skill data={e}/></div>))}
                </ul>
            </div>


        </section>
    )
}
export default SkillsSection;
