function Timeline() {
    const elems = document.getElementsByClassName("timeline-outerblock-left")
    console.log(elems)
    /*
    window.addEventListener('scroll', function (){
        var scroll = window.scrollY - 140
        var idx = Math.floor(scroll / 200)
        idx = Math.min(4,idx)

       for (let i = 0; i < 4; i++) {
            if(i <= idx){
                i%2 === 0 ? elems[i].classList.add("fade-left") : elems[i].classList.add("fade-right")
            } else {
                i%2 === 0 ? elems[i].classList.remove("fade-left") : elems[i].classList.remove("fade-right")
            }
        }

    });*/

    return(
        <div className="ag-format-container">
            <div className="timeline-rowblock">
                <div className="timeline-outerblock-left">
                    <div className="timeline-innerblock-left"></div>
                </div>
            </div>
            <div className="timeline-rowblock">
                <div className="timeline-outerblock-right">
                    <div className="timeline-innerblock-right"></div>
                </div>
            </div>
            <div className="timeline-rowblock">
                <div className="timeline-outerblock-left">
                    <div className="timeline-innerblock-left"></div>
                </div>
            </div>
            <div className="timeline-rowblock">
                <div className="timeline-outerblock-right">
                    <div className="timeline-innerblock-right"></div>
                </div>
            </div>
        </div>
    )
}
export default Timeline;
